  --sidebar-ring: 262 83% 58%;

  --popover: 0 0% 100%;

  --popover-foreground: 240 15% 15%;

  --popover-border: 240 10% 84%;

  --primary: 262 83% 58%;

  --primary-foreground: 0 0% 100%;

  --secondary: 240 10% 86%;

  --secondary-foreground: 240 15% 20%;

  --muted: 240 5% 96%;

  --muted-foreground: 240 4% 46%;

  --accent: 280 65% 60%;

