import { Bell, DoorOpen, PenSquare, Star, Search, ClipboardCheck, User } from "lucide-react";
import FeatureCard from "./FeatureCard";
import SOSButton from "./SOSButton";
import { useLocation } from "wouter";

interface DashboardProps {
  userRole?: "student" | "staff" | "warden";
}

export default function Dashboard({ userRole = "student" }: DashboardProps) {
  const [, navigate] = useLocation();
  const username = localStorage.getItem('username') || 'User';

  const studentFeatures = [
    { icon: Bell, label: "SOS Emergency", color: "text-destructive", path: "/sos", testId: "feature-sos" },
    { icon: DoorOpen, label: "Room Info", color: "text-primary", path: "/rooms", testId: "feature-room" },
    { icon: PenSquare, label: "Complaint", color: "text-chart-2", path: "/complaints", testId: "feature-complaint" },
    { icon: Star, label: "Mess Feedback", color: "text-chart-3", path: "/mess", testId: "feature-mess" },
    { icon: Search, label: "Lost & Found", color: "text-warning", path: "/lost-found", testId: "feature-lost" },
    { icon: ClipboardCheck, label: "Visitors", color: "text-chart-5", path: "/visitors", testId: "feature-visitor" },
  ];

  const wardenFeatures = [
    { icon: User, label: "Dashboard", color: "text-primary", path: "/admin", testId: "feature-admin" },
    { icon: DoorOpen, label: "Allocate Rooms", color: "text-primary", path: "/rooms", testId: "feature-room" },
    { icon: PenSquare, label: "Complaints", color: "text-chart-2", path: "/complaints", testId: "feature-complaint" },
    { icon: Star, label: "Mess Reports", color: "text-chart-3", path: "/mess", testId: "feature-mess" },
    { icon: Search, label: "Lost Items", color: "text-warning", path: "/lost-found", testId: "feature-lost" },
    { icon: ClipboardCheck, label: "Visitor Logs", color: "text-chart-5", path: "/visitors", testId: "feature-visitor" },
  ];

  const features = userRole === "warden" || userRole === "staff" ? wardenFeatures : studentFeatures;

  return (
    <div className="min-h-screen bg-background pb-20">
      <header className="bg-gradient-to-r from-primary to-accent text-primary-foreground p-4 sticky top-0 z-30 shadow-lg">
        <div className="flex items-center justify-between max-w-md mx-auto">
          <div>
            <h1 className="text-xl font-bold">HOSTEL 360°</h1>
            <p className="text-sm opacity-90">Welcome, {username}</p>
          </div>
          <button
            onClick={() => {
              localStorage.clear();
              navigate('/');
            }}
            data-testid="button-logout"
            className="w-10 h-10 bg-white/20 rounded-full flex items-center justify-center hover-elevate active-elevate-2"
          >
            <User className="w-6 h-6" />
          </button>
        </div>
      </header>

      <div className="p-4 max-w-md mx-auto">
        <h2 className="text-lg font-semibold mb-4">Quick Access</h2>
        <div className="grid grid-cols-2 gap-4">
          {features.map((feature) => (
            <FeatureCard
              key={feature.label}
              icon={feature.icon}
              label={feature.label}
              color={feature.color}
              onClick={() => navigate(feature.path)}
              testId={feature.testId}
            />
          ))}
        </div>
      </div>

      {userRole === "student" && <SOSButton />}
    </div>
  );
}

client/src/components/VisitorManagement..content:
import { Plus, User, Phone, Clock, CheckCircle } from "lucide-react";
import { Card } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { useState } from "react";
import VisitorForm from "./VisitorForm";

interface Visitor {
  id: string;
  name: string;
  phone: string;
  purpose: string;
  checkIn: string;
  checkOut?: string;
  status: "active" | "completed";
}

const mockVisitors: Visitor[] = [
  { id: "1", name: "Rajesh Kumar", phone: "+91 98765 43210", purpose: "Parent Visit", checkIn: "10:30 AM", status: "active" },
  { id: "2", name: "Priya Singh", phone: "+91 98765 43211", purpose: "Friend", checkIn: "11:00 AM", checkOut: "2:30 PM", status: "completed" },
  { id: "3", name: "Amit Sharma", phone: "+91 98765 43212", purpose: "Delivery", checkIn: "12:15 PM", checkOut: "12:30 PM", status: "completed" },
];

export default function VisitorManagement() {
  const [visitors, setVisitors] = useState(mockVisitors);
  const [showForm, setShowForm] = useState(false);

  const handleAddVisitor = (newVisitor: any) => {
    const visitor = {
      id: String(visitors.length + 1),
      ...newVisitor
    };
    setVisitors([visitor, ...visitors]);
  };

  const handleCheckout = (visitorId: string) => {
    const now = new Date();
    const checkOutTime = now.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' });

    setVisitors(visitors.map(v => 
      v.id === visitorId 
        ? { ...v, checkOut: checkOutTime, status: "completed" as const }
        : v
    ));
  };

  return (
    <div className="min-h-screen bg-background pb-20">
      <header className="bg-gradient-to-r from-primary to-accent text-primary-foreground p-4 sticky top-0 z-30 shadow-lg">
        <div className="max-w-md mx-auto">
          <h1 className="text-xl font-bold">Visitor Management</h1>
          <p className="text-sm opacity-90">Register & track visitors</p>
        </div>
      </header>

      <div className="p-4 max-w-md mx-auto">
        <Button
          data-testid="button-new-visitor"
          className="w-full mb-4 gap-2"
          onClick={() => setShowForm(true)}
        >
          <Plus className="w-5 h-5" />
          Register New Visitor
        </Button>

        {showForm && (
          <VisitorForm
            onClose={() => setShowForm(false)}
            onSubmit={handleAddVisitor}
          />
        )}

        <div className="space-y-3">
          {visitors.map((visitor) => (
            <Card
              key={visitor.id}
              data-testid={`visitor-card-${visitor.id}`}
              className="hover-elevate active-elevate-2 cursor-pointer overflow-visible"
            >
              <div className="p-4">
                <div className="flex items-start justify-between mb-3">
                  <div className="flex items-start gap-3">
                    <div className="w-10 h-10 bg-primary/10 rounded-full flex items-center justify-center">
                      <User className="w-5 h-5 text-primary" />
                    </div>
                    <div>
                      <h3 className="font-semibold">{visitor.name}</h3>
                      <p className="text-sm text-muted-foreground flex items-center gap-1 mt-1">
                        <Phone className="w-3 h-3" />
                        {visitor.phone}
                      </p>
                    </div>
                  </div>
                  <Badge className={visitor.status === "active" ? "bg-chart-3/10 text-chart-3" : "bg-muted text-muted-foreground"}>
                    {visitor.status === "active" ? "Active" : "Completed"}
                  </Badge>
                </div>

                <div className="space-y-2 text-sm">
                  <div className="flex items-center gap-2 text-muted-foreground">
                    <span className="font-medium">Purpose:</span>
                    <span>{visitor.purpose}</span>
                  </div>
                  <div className="flex items-center gap-2 text-muted-foreground">
                    <Clock className="w-4 h-4" />
                    <span>In: {visitor.checkIn}</span>
                    {visitor.checkOut && <span>• Out: {visitor.checkOut}</span>}
                  </div>
                </div>

                {visitor.status === "active" && (
                  <Button
                    onClick={() => handleCheckout(visitor.id)}
                    variant="outline"
                    size="sm"
                    className="w-full mt-3 gap-2"
                    data-testid={`button-checkout-${visitor.id}`}
                  >
                    <CheckCircle className="w-4 h-4" />
                    Check Out
                  </Button>
                )}
              </div>
            </Card>
          ))}
        </div>
      </div>
    </div>
  );
}

client/src/components/RoomAllocation.tsx.content:
import { Bed, Users, CheckCircle, Clock, XCircle } from "lucide-react";
import { Card } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";

interface Room {
  id: string;
  number: string;
  floor: number;
  capacity: number;
  occupied: number;
  status: "available" | "full" | "maintenance";
}

const mockRooms: Room[] = [
  { id: "1", number: "101", floor: 1, capacity: 4, occupied: 2, status: "available" },
  { id: "2", number: "102", floor: 1, capacity: 4, occupied: 4, status: "full" },
  { id: "3", number: "103", floor: 1, capacity: 3, occupied: 0, status: "maintenance" },
  { id: "4", number: "201", floor: 2, capacity: 4, occupied: 3, status: "available" },
  { id: "5", number: "202", floor: 2, capacity: 4, occupied: 1, status: "available" },
];

const statusConfig = {
  available: { icon: CheckCircle, color: "text-chart-3", bg: "bg-chart-3/10", label: "Available" },
  full: { icon: XCircle, color: "text-muted-foreground", bg: "bg-muted", label: "Full" },
  maintenance: { icon: Clock, color: "text-warning", bg: "bg-warning/10", label: "Maintenance" },
};

export default function RoomAllocation() {
  return (
    <div className="min-h-screen bg-background pb-20">
      <header className="bg-gradient-to-r from-primary to-accent text-primary-foreground p-4 sticky top-0 z-30 shadow-lg">
        <div className="max-w-md mx-auto">
          <h1 className="text-xl font-bold">Room Allocation</h1>
          <p className="text-sm opacity-90">Manage room assignments</p>
        </div>
      </header>

      <div className="p-4 max-w-md mx-auto space-y-3">
        {mockRooms.map((room) => {
          const config = statusConfig[room.status];
          const StatusIcon = config.icon;

          return (
            <Card
              key={room.id}
              data-testid={`room-card-${room.id}`}
              className="hover-elevate active-elevate-2 cursor-pointer overflow-visible"
            >
              <div className="p-4">
                <div className="flex items-start justify-between mb-3">
                  <div className="flex items-center gap-3">
                    <div className="w-12 h-12 bg-primary/10 rounded-md flex items-center justify-center">
                      <Bed className="w-6 h-6 text-primary" />
                    </div>
                    <div>
                      <h3 className="font-semibold text-lg">Room {room.number}</h3>
                      <p className="text-sm text-muted-foreground">Floor {room.floor}</p>
                    </div>
                  </div>
                  <Badge className={`${config.bg} ${config.color} gap-1`}>
                    <StatusIcon className="w-3 h-3" />
                    {config.label}
                  </Badge>
                </div>

                <div className="flex items-center gap-2 text-sm">
                  <Users className="w-4 h-4 text-muted-foreground" />
                  <span className="text-muted-foreground">
                    {room.occupied}/{room.capacity} Occupied
                  </span>
                  <div className="flex-1 bg-muted rounded-full h-2 ml-2">
                    <div 
                      className="bg-primary h-full rounded-full transition-all"
                      style={{ width: `${(room.occupied / room.capacity) * 100}%` }}
                    />
                  </div>
                </div>
              </div>
            </Card>
          );
        })}
      </div>
    </div>
  );
}

