      className={`fixed bottom-20 right-4 w-16 h-16 rounded-full bg-destructive hover:bg-destructive/90 shadow-xl z-50 ${
        isPulsing ? 'animate-pulse' : ''
      }`}
    >
      <Bell className="w-8 h-8 text-destructive-foreground" />
    </Button>
  );
}

